        <thead>
            <tr>
                <th>Id</th>
                <th>Diplome</th>
                <th>Titre</th>
                <th>Anneescoler</th>
                <th>Mention</th>
                <th>Duree</th>
                <th>Anneeterminal</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for formation in formations %}
            <tr>
                <td>{{ formation.id }}</td>
                <td>{{ formation.diplome }}</td>
                <td>{{ formation.titre }}</td>
                <td>{{ formation.anneescoler ? formation.anneescoler|date('Y-m-d') : '' }}</td>
                <td>{{ formation.mention }}</td>
                <td>{{ formation.duree }}</td>
                <td>{{ formation.anneeterminal ? formation.anneeterminal|date('Y-m-d') : '' }}</td>
                <td>
                    <a href=\"{{ path('formation_show', {'id': formation.id}) }}\">show</a>
                    <a href=\"{{ path('formation_edit', {'id': formation.id}) }}\">edit</a>
                </td>
            </tr>
